import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import _ from 'lodash';

const CampaignComparison = () => {
  const [campaignData, setCampaignData] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: 'ROAS', direction: 'desc' });
  const [bestPerformers, setBestPerformers] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const filesResponse = await fetch('http://localhost:5000/api/files');
        if (!filesResponse.ok) {
          throw new Error(`HTTP error! status: ${filesResponse.status}`);
        }
        const files = await filesResponse.json();

        const allData = await Promise.all(
          files.map(async (file) => {
            const response = await fetch(`http://localhost:5000/api/data/${file}`);
            if (!response.ok) {
              throw new Error(`Failed to fetch ${file}`);
            }
            const text = await response.text();
            return Papa.parse(text, {
              header: true,
              dynamicTyping: true,
              skipEmptyLines: true
            }).data;
          })
        );

        const combinedData = allData.flat();
        
        const campaignStats = _(combinedData)
          .groupBy('Campaign Name')
          .map((campaign, name) => {
            const totalImpressions = _.sumBy(campaign, 'Impressions');
            const totalClicks = _.sumBy(campaign, 'Clicks');
            const totalOrders = _.sumBy(campaign, '7 Day Total Orders (#)');
            const totalSpend = _.sumBy(campaign, row => parseFloat(row['Spend'].replace('$', '')) || 0);
            const totalSales = _.sumBy(campaign, row => parseFloat(row['7 Day Total Sales '].replace('$', '')) || 0);

            return {
              name,
              impressions: totalImpressions,
              clicks: totalClicks,
              orders: totalOrders,
              spend: totalSpend,
              sales: totalSales,
              CTR: totalImpressions ? (totalClicks / totalImpressions * 100).toFixed(2) : 0,
              CPC: totalClicks ? (totalSpend / totalClicks).toFixed(2) : 0,
              ACOS: totalSales ? (totalSpend / totalSales * 100).toFixed(2) : 0,
              ROAS: totalSpend ? (totalSales / totalSpend).toFixed(2) : 0,
              conversionRate: totalClicks ? (totalOrders / totalClicks * 100).toFixed(2) : 0
            };
          })
          .value();

        const best = {
          highestROAS: _.maxBy(campaignStats, 'ROAS'),
          lowestACOS: _.minBy(campaignStats.filter(c => parseFloat(c.ACOS) > 0), 'ACOS'),
          highestCTR: _.maxBy(campaignStats, c => parseFloat(c.CTR)),
          highestConversion: _.maxBy(campaignStats, c => parseFloat(c.conversionRate))
        };

        setBestPerformers(best);
        setCampaignData(campaignStats);
        setLoading(false);
      } catch (err) {
        console.error("Error details:", err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'desc' ? 'asc' : 'desc'
    }));
  };

  const sortedData = React.useMemo(() => {
    const sorted = [...campaignData].sort((a, b) => {
      const aValue = parseFloat(a[sortConfig.key]) || 0;
      const bValue = parseFloat(b[sortConfig.key]) || 0;
      return sortConfig.direction === 'asc' ? aValue - bValue : bValue - aValue;
    });
    return sorted;
  }, [campaignData, sortConfig]);

  const formatValue = (value, type) => {
    if (type === 'money') return `$${parseFloat(value).toFixed(2)}`;
    if (type === 'percent') return `${value}%`;
    if (type === 'number') return parseInt(value).toLocaleString();
    return value;
  };

  if (loading) return <div className="p-4">Loading data...</div>;
  if (error) return <div className="p-4 text-red-500">Error: {error}</div>;

  return (
    <div className="space-y-6 p-4">
      {bestPerformers && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <div className="p-4 rounded-lg shadow bg-green-50">
            <div className="text-sm font-bold mb-2">最佳 ROAS</div>
            <p className="font-bold text-xl">{bestPerformers.highestROAS.ROAS}x</p>
            <p className="text-sm text-gray-600 truncate">{bestPerformers.highestROAS.name}</p>
          </div>
          <div className="p-4 rounded-lg shadow bg-blue-50">
            <div className="text-sm font-bold mb-2">最低 ACOS</div>
            <p className="font-bold text-xl">{bestPerformers.lowestACOS.ACOS}%</p>
            <p className="text-sm text-gray-600 truncate">{bestPerformers.lowestACOS.name}</p>
          </div>
          <div className="p-4 rounded-lg shadow bg-purple-50">
            <div className="text-sm font-bold mb-2">最高點擊率</div>
            <p className="font-bold text-xl">{bestPerformers.highestCTR.CTR}%</p>
            <p className="text-sm text-gray-600 truncate">{bestPerformers.highestCTR.name}</p>
          </div>
          <div className="p-4 rounded-lg shadow bg-yellow-50">
            <div className="text-sm font-bold mb-2">最高轉換率</div>
            <p className="font-bold text-xl">{bestPerformers.highestConversion.conversionRate}%</p>
            <p className="text-sm text-gray-600 truncate">{bestPerformers.highestConversion.name}</p>
          </div>
        </div>
      )}

      <div className="bg-white rounded-lg shadow">
        <div className="p-4 border-b">
          <h2 className="text-lg font-bold">廣告活動詳細比較</h2>
        </div>
        <div className="p-4 overflow-x-auto">
          <table className="w-full">
            <thead>
              <tr className="border-b">
                <th className="py-2 px-3 text-left">活動名稱</th>
                <th className="py-2 px-3 text-right cursor-pointer" onClick={() => handleSort('impressions')}>
                  曝光次數
                </th>
                <th className="py-2 px-3 text-right cursor-pointer" onClick={() => handleSort('clicks')}>
                  點擊次數
                </th>
                <th className="py-2 px-3 text-right cursor-pointer" onClick={() => handleSort('CTR')}>
                  CTR
                </th>
                <th className="py-2 px-3 text-right cursor-pointer" onClick={() => handleSort('CPC')}>
                  CPC
                </th>
                <th className="py-2 px-3 text-right cursor-pointer" onClick={() => handleSort('orders')}>
                  訂單數
                </th>
                <th className="py-2 px-3 text-right cursor-pointer" onClick={() => handleSort('spend')}>
                  花費
                </th>
                <th className="py-2 px-3 text-right cursor-pointer" onClick={() => handleSort('sales')}>
                  銷售額
                </th>
                <th className="py-2 px-3 text-right cursor-pointer" onClick={() => handleSort('ACOS')}>
                  ACOS
                </th>
                <th className="py-2 px-3 text-right cursor-pointer" onClick={() => handleSort('ROAS')}>
                  ROAS
                </th>
                <th className="py-2 px-3 text-right cursor-pointer" onClick={() => handleSort('conversionRate')}>
                  轉換率
                </th>
              </tr>
            </thead>
            <tbody>
              {sortedData.map((campaign, index) => (
                <tr key={campaign.name} className={index % 2 === 0 ? 'bg-gray-50' : ''}>
                  <td className="py-2 px-3 text-left">{campaign.name}</td>
                  <td className="py-2 px-3 text-right">{formatValue(campaign.impressions, 'number')}</td>
                  <td className="py-2 px-3 text-right">{formatValue(campaign.clicks, 'number')}</td>
                  <td className="py-2 px-3 text-right">{formatValue(campaign.CTR, 'percent')}</td>
                  <td className="py-2 px-3 text-right">{formatValue(campaign.CPC, 'money')}</td>
                  <td className="py-2 px-3 text-right">{formatValue(campaign.orders, 'number')}</td>
                  <td className="py-2 px-3 text-right">{formatValue(campaign.spend, 'money')}</td>
                  <td className="py-2 px-3 text-right">{formatValue(campaign.sales, 'money')}</td>
                  <td className="py-2 px-3 text-right">{formatValue(campaign.ACOS, 'percent')}</td>
                  <td className="py-2 px-3 text-right">{formatValue(campaign.ROAS, 'number')}x</td>
                  <td className="py-2 px-3 text-right">{formatValue(campaign.conversionRate, 'percent')}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default CampaignComparison;
