import React, { useState, useEffect } from 'react';
import _ from 'lodash';
// 引入數據處理邏輯
import { processCampaignData } from '../../utils/dataTransforms';
import { fetchCampaignData } from '../../utils/api';  

// 導入子組件
import BestPerformersCard from './components/BestPerformersCard';
import CampaignTable from './components/CampaignTable';
import TrendCharts from './components/TrendCharts';
import ControlPanel from './components/ControlPanel';

const CampaignComparison = () => {
  const [campaignData, setCampaignData] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: 'ROAS', direction: 'desc' });
  const [bestPerformers, setBestPerformers] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [timeGranularity, setTimeGranularity] = useState('day');
  const [processedData, setProcessedData] = useState([]);
  const [compareMode, setCompareMode] = useState('combined');

  // CampaignComparison/index.jsx
useEffect(() => {
  const loadData = async () => {
    try {
      setLoading(true);
      
      const data = await fetchCampaignData((message) => {
        console.log(message); // 用於追蹤進度
      });

      const combinedData = allData.flat();
        
      const { stats, best } = processCampaignData(combinedData);
      const trends = processTrendData(combinedData, timeGranularity, compareMode);

      console.log('資料處理完成', { 
        statsLength: stats.length, 
        best, 
        trendsLength: trends?.length 
      });

      setCampaignData(stats);
      setBestPerformers(best);
      setProcessedData(trends || []);
      
    } catch (error) {
      console.error('載入錯誤:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  loadData();
}, []);

  // 當時間粒度或比較模式改變時重新處理數據
  useEffect(() => {
    if (campaignData.length > 0) {
      const newTrendData = processTrendData(
        campaignData, 
        timeGranularity,
        compareMode
      );
      setProcessedData(newTrendData);
    }
  }, [timeGranularity, compareMode, campaignData]);

  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'desc' ? 'asc' : 'desc'
    }));
  };

  if (loading) return <div className="p-4">Loading data...</div>;
  if (error) return <div className="p-4 text-red-500">Error: {error}</div>;

  return (
    <div className="space-y-6 p-4">
      {bestPerformers && (
        <BestPerformersCard bestPerformers={bestPerformers} />
      )}

      <ControlPanel
        timeGranularity={timeGranularity}
        setTimeGranularity={setTimeGranularity}
        compareMode={compareMode}
        setCompareMode={setCompareMode}
      />
      
      <TrendCharts 
        processedData={processedData}
        compareMode={compareMode} 
      />
      
      <TrendCharts processedData={processedData} />
      
      <CampaignTable 
        campaignData={campaignData}
        handleSort={handleSort}
        sortConfig={sortConfig}
      />
    </div>
  );
};

export default CampaignComparison;
