// CampaignComparison/index.jsx
import React, { useState, useEffect, useMemo } from 'react';  // 添加 useMemo
import _ from 'lodash';  // 添加 lodash
import { fetchCampaignData } from '../../utils/api';
import { processCampaignData, processTrendData } from '../../utils/dataTransforms';

// 導入子組件
import BestPerformersCard from './components/BestPerformersCard';
import CampaignTable from './components/CampaignTable';
import TrendCharts from './components/TrendCharts';
import ControlPanel from './components/ControlPanel';

const CampaignComparison = () => {
  // 狀態管理
  const [campaignData, setCampaignData] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: 'ROAS', direction: 'desc' });
  const [bestPerformers, setBestPerformers] = useState(null);
  const [dateRange, setDateRange] = useState({
    startDate: '',
    endDate: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  //const [timeGranularity, setTimeGranularity] = useState('day');
  const [timeGranularity, setTimeGranularity] = useState('hour'); // 預設為小時
  const [processedData, setProcessedData] = useState([]);
  const [compareMode, setCompareMode] = useState('combined');
  const [rawData, setRawData] = useState([]);
  const [availableCampaigns, setAvailableCampaigns] = useState([]);
  const [selectedCampaigns, setSelectedCampaigns] = useState([]);
  

  // 初始數據載入
  useEffect(() => {
    const loadInitialData = async () => {
      try {
        setLoading(true);
        const data = await fetchCampaignData();

        // 先檢查數據
        if (!Array.isArray(data) || data.length === 0) {
          throw new Error('No data available');
        }

        setRawData(data);

        // 設置初始日期範圍
        // 日期處理
        const dates = data
        .map(item => new Date(item["Start Date"]))
        .filter(date => !isNaN(date.getTime())); // 過濾無效日期
        
        if (data.length > 0) {
          const dates = data.map(item => new Date(item["Start Date"]));
          const minDate = new Date(Math.min(...dates));
          const maxDate = new Date(Math.max(...dates));
          
          setDateRange({
            startDate: minDate.toISOString().split('T')[0],
            endDate: maxDate.toISOString().split('T')[0]
          });
        }
        
        // 處理基本統計和最佳表現
        // 分開處理各種數據
        const processedCampaignData = processCampaignData(data);
        setCampaignData(processedCampaignData.stats);
        setBestPerformers(processedCampaignData.best);
                
        // 確保 timeGranularity 和 compareMode 有效
        const validTimeGranularity = timeGranularity || 'day';
        const validCompareMode = compareMode || 'none';

        // 處理趨勢數據
        const trends = processTrendData(data, validTimeGranularity, validCompareMode);
        setProcessedData(trends);
      } catch (error) {
        console.error('載入錯誤:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    loadInitialData();
  }, []);

  // 在數據載入後設置可用的廣告活動
  useEffect(() => {
    if (campaignData.length > 0) {
      const campaigns = campaignData.map(item => item.name);
      setAvailableCampaigns(campaigns);
      setSelectedCampaigns(campaigns); // 預設全選
    }
  }, [campaignData]);

// 當時間粒度、比較模式或日期範圍改變時更新趨勢數據
useEffect(() => {
  if (rawData.length > 0 && dateRange.startDate && dateRange.endDate) {
    // 首先根據日期範圍過濾原始數據
    const filteredRawData = rawData.filter(item => {
      const date = new Date(item["Start Date"]);
      const start = new Date(dateRange.startDate);
      const end = new Date(dateRange.endDate);
      return date >= start && date <= end;
    });

    // 然後處理趨勢數據
    const trends = processTrendData(filteredRawData, timeGranularity, compareMode);
    setProcessedData(trends);
  }
}, [timeGranularity, compareMode, dateRange, rawData]);

  // 修改過濾數據的邏輯
  const filteredData = useMemo(() => {
    if (selectedCampaigns.length === 0 || !processedData) return [];
    
    return processedData.map(data => {
      if (compareMode === 'separate') {
        return {
          ...data,
          campaignData: _.pick(data.campaignData || {}, selectedCampaigns)
        };
      }
      return data;
    });
  }, [processedData, selectedCampaigns, compareMode]);
  // 當時間粒度或比較模式改變時更新趨勢數據
  useEffect(() => {
    if (rawData.length > 0) {
      const trends = processTrendData(rawData, timeGranularity, compareMode);
      setProcessedData(trends);
    }
  }, [timeGranularity, compareMode, rawData]);

  // 處理排序
  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'desc' ? 'asc' : 'desc'
    }));
  };

  // 載入中狀態
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-gray-900"></div>
      </div>
    );
  }

  // 錯誤狀態
  if (error) {
    return (
      <div className="p-4 bg-red-50 border border-red-200 rounded-lg">
        <h3 className="text-red-800 font-semibold">載入失敗</h3>
        <p className="text-red-600">{error}</p>
      </div>
    );
  }

  // 主要內容渲染
  return (
    <div className="space-y-6 p-4">
      {bestPerformers && (
        <BestPerformersCard bestPerformers={bestPerformers} />
      )}

      <ControlPanel
        timeGranularity={timeGranularity}
        setTimeGranularity={setTimeGranularity}
        compareMode={compareMode}
        setCompareMode={setCompareMode}
        campaigns={availableCampaigns}
        selectedCampaigns={selectedCampaigns}
        setSelectedCampaigns={setSelectedCampaigns}
        dateRange={dateRange}
        setDateRange={setDateRange}
      />
      
      <TrendCharts 
        processedData={filteredData}
        compareMode={compareMode}
        timeGranularity={timeGranularity}
      />
      
      <CampaignTable 
        campaignData={campaignData}
        handleSort={handleSort}
        sortConfig={sortConfig}
      />
    </div>
  );
};

export default CampaignComparison;
