import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import _ from 'lodash';
// 引入數據處理邏輯
import { processCampaignData } from '../../utils/dataTransforms';
import { fetchCampaignData } from '../../utils/api';  

// 導入子組件
import BestPerformersCard from './components/BestPerformersCard';
import CampaignTable from './components/CampaignTable';
import TrendCharts from './components/TrendCharts';
import ControlPanel from './components/ControlPanel';

const CampaignComparison = () => {
  const [campaignData, setCampaignData] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: 'ROAS', direction: 'desc' });
  const [bestPerformers, setBestPerformers] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const filesResponse = await fetch('http://localhost:5000/api/files');
        if (!filesResponse.ok) {
          throw new Error(`Failed to fetch files list: ${filesResponse.status}`);
        }
    
        let files = await filesResponse.json();
        if (!Array.isArray(files) || files.length === 0) {
          throw new Error('No files available');
        }
    
        const dataPromises = files.map(file => 
          fetch(`http://localhost:5000/api/data/${file}`)
            .then(response => {
              if (!response.ok) throw new Error(`Failed to fetch ${file}`);
              return response.text();
            })
            .then(text => Papa.parse(text, {
              header: true,
              dynamicTyping: true,
              skipEmptyLines: true
            }).data)
            .catch(error => {
              console.error(`Error processing ${file}:`, error);
              return [];
            })
        );
    
        const allData = await Promise.all(dataPromises);
        const combinedData = allData.flat();
        const processedData = processCampaignData(combinedData);
        
        // 正確設置資料
        setCampaignData(processedData.stats);
        setBestPerformers(processedData.best);
        setLoading(false);
    
      } catch (error) {
        console.error('Fetch Error:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'desc' ? 'asc' : 'desc'
    }));
  };

  if (loading) return <div className="p-4">Loading data...</div>;
  if (error) return <div className="p-4 text-red-500">Error: {error}</div>;

  return (
    <div className="space-y-6 p-4">
      {bestPerformers && <BestPerformersCard bestPerformers={bestPerformers} />}
      <CampaignTable 
        campaignData={campaignData}
        handleSort={handleSort}
        sortConfig={sortConfig}
      />
    </div>
  );
};

export default CampaignComparison;
