// CampaignComparison.jsx
import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import _ from 'lodash';

const CampaignComparison = () => {
  const [campaignData, setCampaignData] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: 'ROAS', direction: 'desc' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const filesResponse = await fetch('http://localhost:5000/api/files');
        if (!filesResponse.ok) {
          throw new Error(`HTTP error! status: ${filesResponse.status}`);
        }
        const files = await filesResponse.json();

        const allData = await Promise.all(
          files.map(async (file) => {
            const response = await fetch(`http://localhost:5000/api/data/${file}`);
            if (!response.ok) {
              throw new Error(`Failed to fetch ${file}`);
            }
            const text = await response.text();
            const result = Papa.parse(text, {
              header: true,
              dynamicTyping: true,
              skipEmptyLines: true
            });
            return result.data;
          })
        );

        const combinedData = allData.flat();
        const processedData = _(combinedData)
          .groupBy('Campaign Name')
          .map((campaign, name) => {
            const totalImpressions = _.sumBy(campaign, 'Impressions');
            const totalClicks = _.sumBy(campaign, 'Clicks');
            const totalOrders = _.sumBy(campaign, '7 Day Total Orders (#)');
            const totalSpend = _.sumBy(campaign, row => parseFloat(row['Spend'].replace('$', '')) || 0);
            const totalSales = _.sumBy(campaign, row => parseFloat(row['7 Day Total Sales '].replace('$', '')) || 0);

            return {
              name,
              impressions: totalImpressions,
              clicks: totalClicks,
              orders: totalOrders,
              spend: totalSpend,
              sales: totalSales,
              CTR: totalImpressions ? (totalClicks / totalImpressions * 100).toFixed(2) : 0,
              CPC: totalClicks ? (totalSpend / totalClicks).toFixed(2) : 0,
              ACOS: totalSales ? (totalSpend / totalSales * 100).toFixed(2) : 0,
              ROAS: totalSpend ? (totalSales / totalSpend).toFixed(2) : 0,
              conversionRate: totalClicks ? (totalOrders / totalClicks * 100).toFixed(2) : 0
            };
          })
          .value();

        setCampaignData(processedData);
        setLoading(false);
      } catch (err) {
        console.error("Error details:", err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'desc' ? 'asc' : 'desc'
    }));
  };

  const sortedData = React.useMemo(() => {
    const sorted = [...campaignData].sort((a, b) => {
      const aValue = parseFloat(a[sortConfig.key]) || 0;
      const bValue = parseFloat(b[sortConfig.key]) || 0;
      return sortConfig.direction === 'asc' ? aValue - bValue : bValue - aValue;
    });
    return sorted;
  }, [campaignData, sortConfig]);

  const formatValue = (value, type) => {
    if (type === 'money') return `$${parseFloat(value).toFixed(2)}`;
    if (type === 'percent') return `${value}%`;
    if (type === 'number') return parseInt(value).toLocaleString();
    return value;
  };

  if (loading) return <div>Loading data...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="p-4">
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white">
          <thead>
            <tr className="bg-gray-100">
              <th className="px-4 py-2 cursor-pointer text-left" onClick={() => handleSort('name')}>
                活動名稱
              </th>
              <th className="px-4 py-2 cursor-pointer text-right" onClick={() => handleSort('impressions')}>
                曝光次數
              </th>
              <th className="px-4 py-2 cursor-pointer text-right" onClick={() => handleSort('clicks')}>
                點擊次數
              </th>
              <th className="px-4 py-2 cursor-pointer text-right" onClick={() => handleSort('CTR')}>
                CTR
              </th>
              <th className="px-4 py-2 cursor-pointer text-right" onClick={() => handleSort('CPC')}>
                CPC
              </th>
              <th className="px-4 py-2 cursor-pointer text-right" onClick={() => handleSort('orders')}>
                訂單數
              </th>
              <th className="px-4 py-2 cursor-pointer text-right" onClick={() => handleSort('spend')}>
                花費
              </th>
              <th className="px-4 py-2 cursor-pointer text-right" onClick={() => handleSort('sales')}>
                銷售額
              </th>
              <th className="px-4 py-2 cursor-pointer text-right" onClick={() => handleSort('ACOS')}>
                ACOS
              </th>
              <th className="px-4 py-2 cursor-pointer text-right" onClick={() => handleSort('ROAS')}>
                ROAS
              </th>
              <th className="px-4 py-2 cursor-pointer text-right" onClick={() => handleSort('conversionRate')}>
                轉換率
              </th>
            </tr>
          </thead>
          <tbody>
            {sortedData.map((campaign, index) => (
              <tr key={campaign.name} className={index % 2 === 0 ? 'bg-gray-50' : ''}>
                <td className="px-4 py-2">{campaign.name}</td>
                <td className="px-4 py-2 text-right">{formatValue(campaign.impressions, 'number')}</td>
                <td className="px-4 py-2 text-right">{formatValue(campaign.clicks, 'number')}</td>
                <td className="px-4 py-2 text-right">{formatValue(campaign.CTR, 'percent')}</td>
                <td className="px-4 py-2 text-right">{formatValue(campaign.CPC, 'money')}</td>
                <td className="px-4 py-2 text-right">{formatValue(campaign.orders, 'number')}</td>
                <td className="px-4 py-2 text-right">{formatValue(campaign.spend, 'money')}</td>
                <td className="px-4 py-2 text-right">{formatValue(campaign.sales, 'money')}</td>
                <td className="px-4 py-2 text-right">{formatValue(campaign.ACOS, 'percent')}</td>
                <td className="px-4 py-2 text-right">{formatValue(campaign.ROAS, 'number')}x</td>
                <td className="px-4 py-2 text-right">{formatValue(campaign.conversionRate, 'percent')}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CampaignComparison;
