import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const TrendCharts = ({ processedData = [] }) => {  // 添加預設值
  // 增加資料檢查
  if (!processedData || !Array.isArray(processedData)) {
    return <div>No trend data available</div>;
  }

  // 只考慮非 null 的 ACOS 值來計算最大值
  const maxACOS = Math.max(...processedData
    .filter(d => d.ACOS !== null)
    .map(d => parseFloat(d.ACOS) || 0));

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
      <div className="bg-white p-4 rounded-lg shadow" style={{ height: '24rem' }}>
        <h3 className="text-lg font-bold mb-4">銷售趨勢</h3>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={processedData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date"
            // 添加日期格式化
            tickFormatter={(value) => {
              if (value.includes(":")) {  // 小時格式
                return value.split(" ")[1];  // 只顯示時間部分
              }
              return value;
            }}            
            />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line 
              type="monotone" 
              dataKey="sales" 
              name="銷售額" 
              stroke="#8884d8" 
              dot={{ r: 1 }}
            />
            <Line 
              type="monotone" 
              dataKey="spend" 
              name="廣告支出" 
              stroke="#82ca9d" 
              dot={{ r: 1 }}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
      
      <div className="bg-white p-4 rounded-lg shadow" style={{ height: '24rem' }}>
        <h3 className="text-lg font-bold mb-4">ACOS 趨勢</h3>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={processedData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis 
              domain={[0, Math.ceil(maxACOS / 100) * 100]} 
              tickFormatter={(value) => `${value}%`}
            />
            <Tooltip formatter={(value) => `${value}%`} />
            <Legend />
            <Line 
              type="monotone" 
              dataKey="ACOS" 
              name="ACOS" 
              stroke="#82ca9d" 
              dot={{ r: 1 }}
              connectNulls={false}  // 當值為 null 時不連接線段
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default TrendCharts;
