import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const TrendCharts = ({ processedData = [], compareMode, timeGranularity }) => {
  if (!processedData || !Array.isArray(processedData)) {
    return <div>No trend data available</div>;
  }

  const formatXAxis = (dateTime) => {
    if (timeGranularity === 'hour') {
      // 如果是小時格式，分割日期和時間
      const [date, time] = dateTime.split(' ');
      return time; // 只顯示時間部分
    } else if (timeGranularity === 'month') {
      return dateTime; // 月份格式保持不變
    } else {
      // 日期格式，轉換為更友好的顯示方式
      const date = new Date(dateTime);
      return date.toLocaleDateString();
    }
  };

  // 只考慮非 null 的 ACOS 值來計算最大值
  const maxACOS = Math.max(...processedData
    .filter(d => d.ACOS !== null)
    .map(d => parseFloat(d.ACOS) || 0));

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
      {/* 銷售趨勢圖表 */}
      <div className="bg-white p-4 rounded-lg shadow" style={{ height: '24rem' }}>
        <h3 className="text-lg font-bold mb-4">銷售趨勢</h3>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={processedData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis 
              dataKey="date"
              tickFormatter={formatXAxis}
              interval={timeGranularity === 'hour' ? 2 : 0}
            />
            <YAxis />
            <Tooltip
              labelFormatter={(label) => formatXAxis(label)}
              formatter={(value) => [`$${parseFloat(value).toFixed(2)}`, '']}
            />
            <Legend />
            {compareMode === "separate" ? (
              // 分開顯示每個活動的數據
              Object.keys(processedData[0]?.campaignData || {}).map((campaign) => (
                <Line
                  key={campaign}
                  type="monotone"
                  dataKey={`campaignData.${campaign}.sales`}
                  name={`${campaign} 銷售額`}
                  stroke={`#${Math.floor(Math.random()*16777215).toString(16)}`}
                  dot={false}
                  strokeWidth={2}
                />
              ))
            ) : (
              <>
                <Line
                  type="monotone"
                  dataKey="sales"
                  name="銷售額"
                  stroke="#8884d8"
                  dot={false}
                  strokeWidth={2}
                />
                <Line
                  type="monotone"
                  dataKey="spend"
                  name="廣告支出"
                  stroke="#82ca9d"
                  dot={false}
                  strokeWidth={2}
                />
              </>
            )}
          </LineChart>
        </ResponsiveContainer>
      </div>

      {/* ACOS 趨勢圖表 */}
      <div className="bg-white p-4 rounded-lg shadow" style={{ height: '24rem' }}>
        <h3 className="text-lg font-bold mb-4">ACOS 趨勢</h3>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={processedData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis 
              dataKey="date"
              tickFormatter={formatXAxis}
              interval={timeGranularity === 'hour' ? 2 : 0}
            />
            <YAxis 
              domain={[0, Math.ceil(maxACOS / 100) * 100]}
              tickFormatter={(value) => `${value}%`}
            />
            <Tooltip 
              labelFormatter={(label) => formatXAxis(label)}
              formatter={(value) => [`${parseFloat(value).toFixed(2)}%`, 'ACOS']}
            />
            <Legend />
            <Line
              type="monotone"
              dataKey="ACOS"
              name="ACOS"
              stroke="#82ca9d"
              dot={false}
              strokeWidth={2}
              connectNulls={false}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default TrendCharts;
