import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import _ from 'lodash';
// 引入數據處理邏輯
import { processCampaignData } from '../../utils/dataTransforms';
import { fetchCampaignData } from '../../utils/api';  

// 導入子組件
import BestPerformersCard from './components/BestPerformersCard';
import CampaignTable from './components/CampaignTable';
import TrendCharts from './components/TrendCharts';
import ControlPanel from './components/ControlPanel';

const CampaignComparison = () => {
  const [campaignData, setCampaignData] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: 'ROAS', direction: 'desc' });
  const [bestPerformers, setBestPerformers] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [timeGranularity, setTimeGranularity] = useState('day');
  const [processedData, setProcessedData] = useState([]);
  const [compareMode, setCompareMode] = useState('combined');

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('1. 開始載入資料');
        setLoading(true);
  
        const filesResponse = await fetch('http://localhost:5000/api/files');
        console.log('2. 檔案列表回應狀態:', filesResponse.status);
        
        if (!filesResponse.ok) {
          throw new Error(`Failed to fetch files list: ${filesResponse.status}`);
        }
    
        let files = await filesResponse.json();
        console.log('3. 取得檔案列表:', files);
    
        if (!Array.isArray(files) || files.length === 0) {
          throw new Error('No files available');
        }
    
        console.log('4. 開始獲取檔案內容');
        const dataPromises = files.map(file => 
          fetch(`http://localhost:5000/api/data/${file}`)
            .then(response => {
              console.log(`5. 檔案 ${file} 回應狀態:`, response.status);
              if (!response.ok) throw new Error(`Failed to fetch ${file}`);
              return response.text();
            })
            .then(text => {
              console.log(`6. 解析 ${file} 內容`);
              return Papa.parse(text, {
                header: true,
                dynamicTyping: true,
                skipEmptyLines: true
              }).data;
            })
            .catch(error => {
              console.error(`Error processing ${file}:`, error);
              return [];
            })
        );
    
        console.log('7. 等待所有檔案處理完成');
        const allData = await Promise.all(dataPromises);
        const combinedData = allData.flat();
        console.log('8. 資料合併完成，開始處理');
        
        const { stats, best, trends } = processCampaignData(combinedData);
        console.log('9. 資料處理完成', { statsLength: stats.length, best, trendsLength: trends?.length });
  
        setCampaignData(stats);
        setBestPerformers(best);
        setProcessedData(trends || []);
        console.log('10. 狀態更新完成');
        
        setLoading(false);
        console.log('11. 載入完成');
    
      } catch (error) {
        console.error('載入錯誤:', error);
        setError(error.message);
        setLoading(false);
      }
    };
  
    fetchData();
  }, []);

  // 當時間粒度改變時重新處理趨勢資料
  useEffect(() => {
    if (campaignData.length > 0) {
      // 可以在這裡添加時間粒度變更時的資料處理邏輯
      // setProcessedData(newTrendData);
    }
  }, [timeGranularity, campaignData]);

  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'desc' ? 'asc' : 'desc'
    }));
  };

  if (loading) return <div className="p-4">Loading data...</div>;
  if (error) return <div className="p-4 text-red-500">Error: {error}</div>;

  return (
    <div className="space-y-6 p-4">
      {bestPerformers && (
        <BestPerformersCard bestPerformers={bestPerformers} />
      )}

      <ControlPanel
        timeGranularity={timeGranularity}
        setTimeGranularity={setTimeGranularity}
        compareMode={compareMode}
        setCompareMode={setCompareMode}
      />
      
      <TrendCharts processedData={processedData} />
      
      <CampaignTable 
        campaignData={campaignData}
        handleSort={handleSort}
        sortConfig={sortConfig}
      />
    </div>
  );
};

export default CampaignComparison;
