import React, { useRef, useEffect } from "react";
import PropTypes from "prop-types";
import Chart from "chart.js/auto";

const ChartComponent = ({
  data,
  options,
  type,
  xAxis,
  filterColumn,
  pointSizeColumn,
}) => {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      const chartInstance = new Chart(chartRef.current, {
        type: type,
        data: data,
        options: {
          ...options,
          plugins: {
            tooltip: {
              callbacks: {
                label: function (context) {
                  let label = context.dataset.label || "";
                  if (label) {
                    label += ": ";
                  }
                  if (context.parsed.y !== null) {
                    label += context.parsed.y;
                  }
                  const xValue =
                    context.raw.x !== undefined
                      ? `X軸 (${xAxis}): ${context.raw.x}`
                      : "";
                  const filterValue = filterColumn
                    ? `篩選條件 (${filterColumn}): ${context.raw[filterColumn]}`
                    : "";
                  const pointSizeValue = pointSizeColumn
                    ? `點大小 (${pointSizeColumn}): ${context.raw[pointSizeColumn]}`
                    : "";

                  return [label, xValue, filterValue, pointSizeValue]
                    .filter(Boolean)
                    .join("\n");
                },
              },
            },
          },
        },
      });

      return () => {
        chartInstance.destroy();
      };
    }
  }, [data, options, type, xAxis, filterColumn, pointSizeColumn]);

  return <canvas ref={chartRef}></canvas>;
};

ChartComponent.propTypes = {
  data: PropTypes.object.isRequired,
  options: PropTypes.object,
  type: PropTypes.string.isRequired,
  xAxis: PropTypes.string.isRequired,
  filterColumn: PropTypes.string,
  pointSizeColumn: PropTypes.string,
};

export default ChartComponent;
