import React from "react";

const DataTable = ({ columns, data, onFilterChange }) => {
  return (
    <table className="table">
      <thead>
        <tr>
          {columns.map((col) => (
            <th key={col}>
              {col}
              <input
                type="text"
                onChange={(e) => onFilterChange(col, e.target.value)}
                placeholder={`篩選 ${col}`}
              />
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((row, rowIndex) => (
          <tr key={rowIndex}>
            {columns.map((col) => (
              <td key={col}>{row[col]}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default DataTable;
