import React, { useState, useEffect } from "react";
import { fetchTables, fetchTableData } from "./services/api";
import Dropdown from "./components/Dropdown";
import ChartComponent from "./components/ChartComponent";
import DataTable from "./components/DataTable";

const App = () => {
  const [tables, setTables] = useState([]);
  const [selectedTable, setSelectedTable] = useState(
    "240621_Sponsored_Products_Search_term_report"
  ); // 預設表格
  const [columns, setColumns] = useState([]);
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({});
  const [xAxis, setXAxis] = useState(""); // 初始化為空
  const [yAxis, setYAxis] = useState(""); // 初始化為空

  // Fetch tables on mount
  useEffect(() => {
    fetchTables()
      .then((response) => {
        console.log("Fetched tables:", response.data);
        setTables(response.data);
        if (response.data.length > 0 && !selectedTable) {
          setSelectedTable(response.data[0]);
        }
      })
      .catch((error) => console.error("Error fetching tables:", error));
  }, []);

  // Fetch table data when selectedTable changes
  useEffect(() => {
    if (selectedTable) {
      fetchTableData(selectedTable)
        .then((responseData) => {
          if (responseData) {
            console.log("Fetched table data:", responseData);
            if (
              Array.isArray(responseData.columns) &&
              Array.isArray(responseData.data)
            ) {
              setColumns(responseData.columns);
              const sanitizedData = responseData.data.map((row) => {
                const sanitizedRow = {};
                for (const key in row) {
                  sanitizedRow[key] =
                    typeof row[key] === "number" && isNaN(row[key])
                      ? 0
                      : row[key];
                }
                return sanitizedRow;
              });
              console.log("Sanitized data:", sanitizedData);
              setData(sanitizedData);

              // 設置預設 X 軸和 Y 軸並更新圖表數據
              if (!xAxis && !yAxis) {
                setXAxis("Date");
                setYAxis("TACoS");
              }
            } else {
              console.error("Invalid data structure:", responseData);
            }
          } else {
            console.error("No data returned from API");
          }
        })
        .catch((error) => console.error("Error fetching table data:", error));
    }
  }, [selectedTable, xAxis, yAxis]); // 添加 selectedTable, xAxis, yAxis 作為依賴項

  // Update chart data when xAxis, yAxis, or data changes
  useEffect(() => {
    if (xAxis && yAxis && data.length > 0) {
      const chartData = {
        labels: data.map((row) => row[xAxis]),
        datasets: [
          {
            label: yAxis,
            data: data.map((row) => row[yAxis]),
            backgroundColor: "rgba(75, 192, 192, 0.6)",
            borderWidth: 4,
            pointRadius: 5,
            pointHoverRadius: 7,
            barThickness: 30, // 固定條形寬度
            maxBarThickness: 30, // 設置最大條形寬度
          },
        ],
      };
      console.log("Chart data:", chartData);
      setChartData(chartData);
    }
  }, [xAxis, yAxis, data]); // 添加 xAxis, yAxis, data 作為依賴項

  if (tables.length === 0) {
    return <div>Loading...</div>;
  }

  const renderDropdowns = () => (
    <>
      <Dropdown
        id="table-select"
        label="選擇表格"
        options={tables.map((table) => ({ label: table, value: table }))}
        value={selectedTable}
        onChange={(value) => {
          setSelectedTable(value);
        }}
      />
      <Dropdown
        id="x-axis-select"
        label="選擇X軸"
        options={
          columns.length > 0
            ? columns.map((col) => ({ label: col, value: col }))
            : [{ label: "Loading...", value: "" }]
        }
        value={xAxis}
        onChange={(value) => {
          setXAxis(value);
        }}
      />
      <Dropdown
        id="y-axis-select"
        label="選擇Y軸"
        options={
          columns.length > 0
            ? columns.map((col) => ({ label: col, value: col }))
            : [{ label: "Loading...", value: "" }]
        }
        value={yAxis}
        onChange={(value) => {
          setYAxis(value);
        }}
      />
    </>
  );

  const renderChart = () => {
    if (data.length > 0 && xAxis && yAxis) {
      // 確保 X 軸和 Y 軸被設置
      return (
        <ChartComponent
          data={chartData}
          options={{ responsive: true, maintainAspectRatio: false }}
          type="bar"
        />
      );
    } else {
      return <div>Loading chart data...</div>;
    }
  };

  const renderDataTable = () => {
    if (columns.length > 0 && data.length > 0) {
      return <DataTable columns={columns} data={data} />;
    } else {
      return <div>Loading table data...</div>;
    }
  };

  return (
    <div className="container">
      <h1 className="mb-4">EYEGLAD AmazonADs</h1>
      {renderDropdowns()}
      {renderChart()}
      <h2 className="mt-4">數據表</h2>
      {renderDataTable()}
    </div>
  );
};

export default App;
