import React from "react";
import { useTable, useExpanded } from "react-table";
import PropTypes from "prop-types";

const DataTable = ({ columns, data }) => {
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable(
      {
        columns,
        data,
      },
      useExpanded, // 使用行擴展功能
      (hooks) => {
        hooks.visibleColumns.push((columns) => [
          {
            id: "expander", // 注意這裡需要一個唯一的 id
            Header: ({ getToggleAllRowsExpandedProps }) => (
              <span {...getToggleAllRowsExpandedProps()}>Expand</span>
            ),
            Cell: ({ row }) => (
              <span {...row.getToggleRowExpandedProps()}>
                {row.isExpanded ? "👇" : "👉"}
              </span>
            ),
          },
          ...columns,
        ]);
      }
    );

  return (
    <table {...getTableProps()} className="table">
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()}>{column.render("Header")}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row) => {
          prepareRow(row);
          return (
            <React.Fragment key={row.id}>
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                ))}
              </tr>
              {row.isExpanded ? (
                <tr>
                  <td colSpan={columns.length}>
                    {/* 渲染擴展內容 */}
                    <ExpandedRowContent row={row.original} />
                  </td>
                </tr>
              ) : null}
            </React.Fragment>
          );
        })}
      </tbody>
    </table>
  );
};

const ExpandedRowContent = ({ row }) => {
  return (
    <div>
      <p>
        <strong>Additional Information:</strong>
      </p>
      <p>Other Column 1: {row.otherColumn1}</p>
      <p>Other Column 2: {row.otherColumn2}</p>
      <p>Other Column 3: {row.otherColumn3}</p>
      {/* Add more fields as needed */}
    </div>
  );
};

ExpandedRowContent.propTypes = {
  row: PropTypes.object.isRequired,
};

DataTable.propTypes = {
  columns: PropTypes.arrayOf(PropTypes.object).isRequired,
  data: PropTypes.arrayOf(PropTypes.object).isRequired,
};

export default DataTable;
